import{_ as e,o,c as t,a2 as r}from"./chunks/framework.BGfUC0LL.js";const k=JSON.parse('{"title":"kafka常用命令记录","description":"","frontmatter":{},"headers":[],"relativePath":"java/middleware/kafka常用命令记录.md","filePath":"java/middleware/kafka常用命令记录.md","lastUpdated":1770864474000}'),i={name:"java/middleware/kafka常用命令记录.md"};function c(p,a,s,n,d,l){return o(),t("div",null,[...a[0]||(a[0]=[r('<h1 id="kafka常用命令记录" tabindex="-1">kafka常用命令记录 <a class="header-anchor" href="#kafka常用命令记录" aria-label="Permalink to &quot;kafka常用命令记录&quot;">​</a></h1><blockquote><p>环境信息：MacOS 13.4.1</p><p>Kafka版本：Homebrew Kafka_3.4.0</p></blockquote><h2 id="topic" tabindex="-1">topic <a class="header-anchor" href="#topic" aria-label="Permalink to &quot;topic&quot;">​</a></h2><h5 id="新建topic" tabindex="-1">新建topic <a class="header-anchor" href="#新建topic" aria-label="Permalink to &quot;新建topic&quot;">​</a></h5><p><code>kafka-topics --create --topic topic-name --partitions 4 --replication-factor 2 --bootstrap-server localhost:9092</code></p><h5 id="查看所有topic" tabindex="-1">查看所有topic <a class="header-anchor" href="#查看所有topic" aria-label="Permalink to &quot;查看所有topic&quot;">​</a></h5><p><code>kafka-topics --bootstrap-server 127.0.0.1:9092 --list</code></p><h5 id="查看topic信息" tabindex="-1">查看topic信息 <a class="header-anchor" href="#查看topic信息" aria-label="Permalink to &quot;查看topic信息&quot;">​</a></h5><p><code>kafka-topics --bootstrap-server 127.0.0.1:9092 --describe --topic topic-name</code></p><h5 id="修改topic分区数" tabindex="-1">修改topic分区数 <a class="header-anchor" href="#修改topic分区数" aria-label="Permalink to &quot;修改topic分区数&quot;">​</a></h5><p><code>kafka-topics --bootstrap-server 127.0.0.1:9092 --alter --partitions 2 --topic topic-name</code></p><h5 id="删除topic" tabindex="-1">删除topic <a class="header-anchor" href="#删除topic" aria-label="Permalink to &quot;删除topic&quot;">​</a></h5><p><code>kafka-topics --bootstrap-server 127.0.0.1:9092 --delete --topic topic-name</code></p><h5 id="清空topic的消息" tabindex="-1">清空topic的消息 <a class="header-anchor" href="#清空topic的消息" aria-label="Permalink to &quot;清空topic的消息&quot;">​</a></h5><blockquote><p>给topic保留消息时间改为1s，然后等topic中的消息被自动删除，再删除该配置</p><p>删除topic也可以实现清空消息</p></blockquote><p><code>kafka-configs --bootstrap-server 127.0.0.1:9092 --entity-type topics --alter --entity-name example --add-config retention.ms=1000</code></p><p><code>kafka-configs --bootstrap-server 127.0.0.1:9092 --entity-type topics --alter --entity-name example --delete-config retention.ms</code></p><h2 id="消费者组" tabindex="-1">消费者组 <a class="header-anchor" href="#消费者组" aria-label="Permalink to &quot;消费者组&quot;">​</a></h2><h5 id="查看所有消费者组" tabindex="-1">查看所有消费者组 <a class="header-anchor" href="#查看所有消费者组" aria-label="Permalink to &quot;查看所有消费者组&quot;">​</a></h5><p><code>kafka-consumer-groups --bootstrap-server 127.0.0.1:9092 --list</code></p><h5 id="查看消费者组详情" tabindex="-1">查看消费者组详情 <a class="header-anchor" href="#查看消费者组详情" aria-label="Permalink to &quot;查看消费者组详情&quot;">​</a></h5><p><code>kafka-consumer-groups --bootstrap-server 127.0.0.1:9092 --describe --group topic-name</code></p><h5 id="查看消费者组里具体成员" tabindex="-1">查看消费者组里具体成员 <a class="header-anchor" href="#查看消费者组里具体成员" aria-label="Permalink to &quot;查看消费者组里具体成员&quot;">​</a></h5><p><code>kafka-consumer-groups --bootstrap-server 127.0.0.1:9092 --describe --members --group topic-name</code></p><h2 id="消费者console" tabindex="-1">消费者console <a class="header-anchor" href="#消费者console" aria-label="Permalink to &quot;消费者console&quot;">​</a></h2><h5 id="查看topic中的所有消息" tabindex="-1">查看topic中的所有消息 <a class="header-anchor" href="#查看topic中的所有消息" aria-label="Permalink to &quot;查看topic中的所有消息&quot;">​</a></h5><p><code>kafka-console-consumer --bootstrap-server 127.0.0.1:9092 --topic topic-name --from-beginning</code></p><h5 id="指定分区、offset的消息" tabindex="-1">指定分区、offset的消息 <a class="header-anchor" href="#指定分区、offset的消息" aria-label="Permalink to &quot;指定分区、offset的消息&quot;">​</a></h5><p><code>kafka-console-consumer --bootstrap-server 127.0.0.1:9092 --topic topic-name --partition 0 --offset 1</code></p>',29)])])}const f=e(i,[["render",c]]);export{k as __pageData,f as default};
